import React, {useState, useRef} from 'react'

function RefSample() {
    //refNum = {current : 0}
    const refNum = useRef(0);

    //stateNum = 0;
    const [stateNum, setStateNum] = useState(0);

    console.log('렌더링 발생');

    return(
        <div>
            <h1>refNum : {refNum.current} </h1>
            <h1>stateNum : {stateNum} </h1>
            <button onClick={increaseRefNum}>refNum + 1</button>
        </div>
    )
}
